const mapStateToProps = state => {
  return {
    auth: state.authReducer,
  };
};

const mapDispatchToProps = dispatch => {
  return {
    logout: () => dispatch(authActions.logout()),
  }
}

export default connect(mapStateToProps, mapDispatchToProps, null, { pure: false })(Drawer);
